import osimport configparserfrom common import getPathInfopath = getPathInfo.get_Path()config_path = os.path.join(path, 'config.ini')config = configparser.ConfigParser()config.read(config_path, encoding='utf-8')class ReadConfig():    def get_http(self, name):        value = config.get('HTTP', name)        return value    def get_excel(self, name):        value = config.get("CASE", name)        return value