import xlrdfrom common.readExcelData import readExcelDataclass readExcel(object):    def __init__(self, path):        self.path = path    @property    def getSheet(self):        # 获取索引        xl = xlrd.open_workbook(self.path)        sheet = xl.sheet_by_name("case_admin_api")        return sheet    @property    def getRows(self):        # 获取行数        row = self.getSheet.nrows        return row    @property    def getCol(self):        # 获取列数        col = self.getSheet.ncols        return col    # 以下是分别获取每一列的数值    @property    def getId(self):        TestId = []        for i in range(1, self.getRows):            TestId.append(self.getSheet.cell_value(i, 0))        return TestId    @property    def getName(self):        TestName = []        for i in range(1, self.getRows):            TestName.append(self.getSheet.cell_value(i, 1))        return TestName    @property    def getUrl(self):        TestUrl = []        for i in range(1, self.getRows):            TestUrl.append(self.getSheet.cell_value(i, 2))        return TestUrl    @property    def getMethod(self):        TestMethod = []        for i in range(1, self.getRows):            TestMethod.append(self.getSheet.cell_value(i, 3))        return TestMethod    @property    def getData(self):        TestData = []        for i in range(1, self.getRows):            sheetName = self.getSheet.cell_value(i, 4)            if (sheetName == ''):                TestData.append('null')                continue            TestData.append(readExcelData(path=self.path, sheetName=sheetName).getData)        return TestData    @property    def getSheetName(self):        TestSheetName = []        for i in range(1, self.getRows):            TestSheetName.append(self.getSheet.cell_value(i, 4))        return TestSheetName    @property    def getHeaders(self):        TestHeaders = []        for i in range(1, self.getRows):            TestHeaders.append(self.getSheet.cell_value(i, 5))        return TestHeaders    @property    def getCaseId(self):        TestCaseId = []        for i in range(1, self.getRows):            TestCaseId.append(self.getSheet.cell_value(i, 0))        return TestCaseId