import xlrdclass readExcelData(object):    def __init__(self, path, sheetName):        self.path = path        self.sheetName = sheetName    @property    def getSheetData(self):        # 获取索引        xl = xlrd.open_workbook(self.path)        sheet = xl.sheet_by_name(self.sheetName)        return sheet    @property    def getRows(self):        # 获取行数        row = self.getSheetData.nrows        return row    @property    def getCol(self):        # 获取列数        col = self.getSheetData.ncols        return col    @property    def getCaseId(self):        TestCaseId = []        for i in range(1, self.getRows):            TestCaseId.append(self.getSheetData.cell_value(i, 0))        return TestCaseId    @property    def getExcepted(self):        excepted = []        for i in range(1, self.getRows):            excepted.append(self.getSheetData.cell_value(i, self.getCol - 1))        return excepted    @property    def getData(self):        totalArray = []        title = []        for i in range(1, self.getCol - 1):            title.append(self.getSheetData.cell(0, i).value)        for rowIndex in range(1, self.getRows):            data = {}            for colIndex in range(1, self.getCol - 1):                s_type = self.getSheetData.cell(rowIndex, colIndex).ctype                s = self.getSheetData.cell(rowIndex, colIndex).value                if s_type == 2:                    s = int(s)                data[title[colIndex - 1]] = s                totalArray.append(data)        return totalArray